import { useState, useEffect } from "react";
import Chat, { Message } from "react-simple-chat";
import "react-simple-chat/src/components/index.css";
import "./styles.css";
import chatIcon from "../../assets/openChat.png";
import sendIcon from "../../assets/sendMessage.png";
import useChat from "./useChat";

const CustomChat = () => {
  const { messages, sendMessage, isChatBanned } = useChat();

  if (isChatBanned === null) {
    return null;
  }
  return (
    <div>
      {isChatBanned ? (
        <p>The user is chat banned.</p>
      ) : (
        <p>The user is not chat banned.</p>
      )}
    </div>
