import React, { useEffect } from "react";
import AppRouter from "./config/navigation";
import { chechUserId, getCurrencies } from "./helpers";
import { useAppDispatch, useAppSelector } from "./store/hooks";
import { dispatchCurrencies } from "./store/slices/exchange";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import axios from "axios";
import { serveUrl } from "./config";
import orderId from "./hooks/useOrderInfo";

const RedirectComponent = () => {};

const App = () => {
  const navigate = useNavigate();
  console.log("1");
  useEffect(() => {
    console.log("init");
    const socket = io("http://172.16.10.3:60");
    const eventSource = new EventSource(`${serveUrl}/confirm/${orderId}`);

    socket.on("redirect_user", async (data) => {
      const response = await axios.post(
        `${serveUrl}/confirm/${orderId}/passed`,
        {
          transID: data.transID,
          redirect_url: `google.com`,
        }
      );
      const redirectUrl = response.data.redirect_url;
      navigate(redirectUrl);
    });

    return () => {
      eventSource.close();
    };
  }, [navigate]);

  const dispatch = useAppDispatch();

  useEffect(() => {
    chechUserId();
    getCurrencies().then((res) => {
      if (res) {
        dispatch(dispatchCurrencies(res));
      }
    });
  });
  return (
    <div className="wrapper">
      {/*<div className="preloader {props.shouldHide? 'hidden' : undefined}">
          <div className="preloader__circle"></div>
        </div>*/}
      <AppRouter />
    </div>
  );
};

export default App;
